if(document.querySelectorAll(".js-button")){var buttons=document.querySelectorAll(".js-button");buttons.forEach(function(r,e,t){r.addEventListener("click",function(e){e.preventDefault();var t=r.dataset.button,o=document.querySelector("[data-modal="+t+"]");o.classList.toggle("modal-display"),o.classList.remove("modal-error")})})}else alert("Отсутствует класс js-button у кнопок отвечающих за вызов модальных окон.");if(document.querySelector(".modal_write-us")){var modal=document.querySelector(".modal_write-us"),form=modal.querySelector("form"),name=form.querySelector("[name=name]"),email=form.querySelector("[name=email]"),someText=form.querySelector("[name=text-of-the-letter]");form.addEventListener("submit",function(e){name.value||email.value||someText.value||(e.preventDefault(),modal.classList.remove("modal-error"),modal.offsetWidth=modal.offsetWidth,modal.classList.add("modal-error"))})}
