if(document.querySelectorAll(".js-button")){var buttons=document.querySelectorAll(".js-button");buttons.forEach(function(o,t,e){o.addEventListener("click",function(t){t.preventDefault();var e=o.dataset.button;document.querySelector("[data-modal="+e+"]").classList.toggle("modal-display")})})}else alert("Отсутствует класс js-button у кнопок отвечающих за вызов модальных окон.");
